import React, { useState, useEffect } from "react";
import {
  BrowserRouter as Router,
  Route,
  Routes,
  Navigate,
} from "react-router-dom";

import UserInformation from "./routes/userInfo";
import ProductDetails from "./routes/ProductDetails";   
import PeyajRoshun from "./routes/PeyajRoshun";
import HomePage from "./routes/HomePage";
import ProductCategoryWise from "./routes/ProductCategoryWise";
import UserDetailsPage from "./routes/UserDetailsPage";
import LoginPage from "./routes/LoginPage";
import SignUpPage from "./routes/SignUp";
import SearchProducts from "./routes/SearchProducts";
import Cart from "./routes/Cart";
import Home from "./routes/HomePage";

const App = () => {
  const [isAuthenticated, setIsAuthenticated] = useState(false);

  const setAuth = (boolean) => {
    setIsAuthenticated(boolean);
  };

  const isAuth = async () => {
    try {
      const response = await fetch("http://localhost:3006/auth/is-verify", {
        method: "GET",
        headers: { token: localStorage.token },
      });

      const parseRes = await response.json();
      parseRes === true ? setIsAuthenticated(true) : setIsAuthenticated(false);
    } catch (err) {
      console.error(err.message);
    }
  };

  useEffect(() => {
    isAuth();
  });

  return (
  <div>
    <Router>
      <Routes>
        {/*if user is authenticated then show home page else show login page */}
        <Route path="/" element={isAuthenticated?(<HomePage/>):(<LoginPage/>)}/>
        <Route path="/productDetails/:id" element={isAuthenticated?(<ProductCategoryWise/>):(<Navigate to="/login"/>)}/>
        <Route path="/productscategorywise/:id" element={<ProductCategoryWise/>} />
        <Route path="/userProfile" element={<UserInformation/>} />
        <Route path="/searchProducts" element={<SearchProducts/>} />
        <Route path="/userDetails/:id" element={<UserDetailsPage setAuth={setAuth} />} />
        <Route path="/login" element={<LoginPage setAuth={setAuth} />} />
        <Route path="/signup" element={<SignUpPage setAuth={setAuth} />} />
        <Route path="/cart" element={<Cart/>}/>
      </Routes>
    </Router>
  </div>
  );

}
export default App;
