import React, { Fragment, useEffect, useState } from 'react';
import { useParams, Link } from 'react-router-dom';
import '../styles/ProductsCategoryWise.css'; // Import the CSS file

const ProductCategoryWise = ({ setAuth }) => {
  const { categoryid } = useParams();
  const [products, setProducts] = useState([]);

  useEffect(() => {
    const fetchProducts = async () => {
      try {
        console.log('Fetching products for category:', categoryid);
        const response = await fetch(`http://localhost:3006/products/${categoryid}`);
        const data = await response.json();
        console.log('Response:', response);
        console.log('Data:', data);
        setProducts(data);
      } catch (error) {
        console.error('Error fetching products:', error);
      }
    };

    fetchProducts();
  }, [categoryid]);

  return (
    <Fragment>
      <div className="container mt-4" style={{ marginTop: '200px' }}> {/* Adding margin top of 200px */}
        <h1 className="text-primary">ProductCategoryWise</h1> {/* Using Bootstrap's primary color */}
        <p className="text-info">Category ID: {categoryid}</p> {/* Using Bootstrap's info color */}
        <div className="row">
          {products.map((product) => (
            <div key={product.productid} className="col-md-4 mb-4">
              <div className="card">
                <div className="card-body">
                  <h5 className="card-title text-danger">{product.name}</h5> {/* Using Bootstrap's danger color */}
                  <ul className="list-group list-group-flush">
                    <li className="list-group-item">Price: <span className="text-success">{product.price}</span></li> {/* Using Bootstrap's success color */}
                    <li className="list-group-item">Quantity in Stock: <span className="text-warning">{product.quantityinstock}</span></li> {/* Using Bootstrap's warning color */}
                    <li className="list-group-item">Category ID: <span className="text-secondary">{product.categoryid}</span></li> {/* Using Bootstrap's secondary color */}
                  </ul>
                  <Link to={`/productDetails/${product.productid}`} className="btn btn-dark mt-2">View Details</Link> {/* Using Bootstrap's dark color */}
                </div>
              </div>
            </div>
          ))}
        </div>
      </div>
    </Fragment>
  );
};

export default ProductCategoryWise;
