import React, { useState, useEffect } from "react";
import CategoryList from "../components/CategoryList";
import UserInformation from "../routes/userInfo";
import { Link } from "react-router-dom";
import "../styles/Home.css";

const Home = ({ setAuth }) => {
  const [isLoggedIn, setIsLoggedIn] = useState(true); // State to track login status
   [firstName, setFirstName] = useState("");
  const [laconststName, setLastName] = useState("");
  const [email, setEmail] = useState("");
  const [city, setCity] = useState("");
  const [district, setDistrict] = useState("");
  const [showUserInfo, setShowUserInfo] = useState(false); // State to track whether to show user info

  async function getUserInfo() {
    try {
      const response = await fetch("http://localhost:3006/dashboard/", {
        method: "GET",
        headers: { token: localStorage.token },
      });

      const userData = await response.json();
      console.log(userData);
      setFirstName(userData.firstname);
      setLastName(userData.lastname);
      setEmail(userData.email);
      setCity(userData.city);
      setDistrict(userData.district);
    } catch (err) {
      console.error(err.message);
    }
  }

  useEffect(() => {
    getUserInfo();
  }, []);

  const handleLogout = () => {
    try {
      localStorage.removeItem("token");
      setAuth(false); // Update authentication status
      setIsLoggedIn(false); // Update login status
    } catch (err) {
      console.error(err.message);
    }
  };

  return (
    <div>
      <nav className="navbar">
        <div className="navbar__left">
          <button onClick={() => setShowUserInfo(!showUserInfo)}>
            User Profile
          </button>
          <Link to="/userInfo">User Details</Link>
        </div>
        <div className="navbar__right">
          <select>
            <option value="category1">Category 1</option>
            <option value="category2">Category 2</option>
            <option value="category3">Category 3</option>
          </select>
          <input type="text" placeholder="Search..." />
        </div>
      </nav>

      {/* Conditional rendering of user information */}
      {showUserInfo && (
        <UserInformation
          firstName={firstName}
          lastName={lastName}
          email={email}
          city={city}
          district={district}
        />
      )}

      {/* Other content of the Home component */}
      {/* For example, CategoryList or other components */}
    </div>
  );
};

export default Home;
