import React, { useState, useEffect } from 'react';
import { useUserContext } from '../App';

const Cart = () => {
  const [products, setProducts] = useState([]);
  const { userId } = useUserContext();

  useEffect(() => {
    async function fetchCartProducts() {
      try {
        const response = await fetch(`http://localhost:3006/api/cart/${userId}`);
        if (!response.ok) {
          throw new Error('Failed to fetch products');
        }
        const productsData = await response.json();
        setProducts(productsData);
      } catch (error) {
        console.error('Error fetching products:', error);
      }
    }
    fetchCartProducts();
  }, [userId]);

  const handleRemoveProduct = async (productId) => {
    try {
      const response = await fetch(`http://localhost:3006/api/removeFromCart/${productId}/user/${userId}`, {
        method: 'DELETE',
      });
      if (!response.ok) {
        throw new Error('Failed to delete product');
      }
      setProducts(products.filter(product => product.productid !== productId));
    } catch (error) {
      console.error('Error deleting product:', error);
    }
  };

  return (
    <div className="container mt-4" style={{ marginTop: '400px' }}> {/* Add margin-top inline style */}
      <h1>Cart</h1>
      {products.map(product => (
        <div key={product.productid} className="card mb-3 col-md-6">
          <div className="card-body">
            <h5 className="card-title">{product.name}</h5>
            <p className="card-text">${product.price}</p>
            <button className="btn btn-danger" onClick={() => handleRemoveProduct(product.productid)}>Remove</button>
          </div>
        </div>
      ))}
    </div>
  );
};

export default Cart;
