import React, { useState, useEffect } from 'react';
import { useParams } from 'react-router-dom'; // Import useParams to access URL parameters

const OrderDetails = () => {
  const [orderDetails, setOrderDetails] = useState([]);
  const { orderId } = useParams(); // Get the orderId parameter from the URL

  useEffect(() => {
    async function fetchOrderDetails() {
      try {
        const response = await fetch(`http://localhost:3006/order/${orderId}`); // Fetch order details using orderId
        if (!response.ok) {
          throw new Error('Failed to fetch order details');
        }
        const orderDetailsData = await response.json();
        setOrderDetails(orderDetailsData);
      } catch (error) {
        console.error('Error fetching order details:', error);
      }
    }
    fetchOrderDetails();
  }, [orderId]);

  return (
    <div>
      <h2>Order Details</h2>
      {orderDetails.length > 0 ? (
        <div className="order-details-container">
          {orderDetails.map((detail) => (
            <div key={detail.orderid} className="order-detail">
              <p><strong>Product ID:</strong> {detail.productid}</p>
              <p><strong>Quantity:</strong> {detail.Quantity}</p>
              <p><strong>Price:</strong> {detail.Price}</p>
            </div>
          ))}
        </div>
      ) : (
        <p>Loading...</p>
      )}
    </div>
  );
};

export default OrderDetails;
