import React, { useState, useEffect, useContext } from "react";
import { useUserContext } from "../App";
import { Link } from "react-router-dom"; // Import Link from react-router-dom
import "../styles/EditPge.css"; // Import the CSS file
const EditPage = () => {
    const { userId, setUserId } = useUserContext(); // Access userId from context

    const [firstName, setFirstName] = useState("");
    const [lastName, setLastName] = useState("");
    const [email, setEmail] = useState("");
    const [city, setCity] = useState("");
    const [district, setDistrict] = useState("");
    const [isProfileUpdated, setIsProfileUpdated] = useState(false); // Add new state to show if profile is updated

    useEffect(() => {
        async function getUserInfo() {
            try {
                const response = await fetch("http://localhost:3006/dashboard/", {
                    method: "GET",
                    headers: { token: localStorage.token },
                });
                const userData = await response.json();
                console.log(userData);
                // Update states with received data
                setUserId(userData.userid);
                setFirstName(userData.firstname);
                setLastName(userData.lastname);
                setEmail(userData.email);
                setCity(userData.city);
                setDistrict(userData.district);
            } catch (err) {
                console.log("Could not fetch user info");
            }
        }
        getUserInfo();
    }, []);

    const handleFirstNameChange = (e) => {
        setFirstName(e.target.value);
    };

    const handleLastNameChange = (e) => {
        setLastName(e.target.value);
    };

    const handleEmailChange = (e) => {
        setEmail(e.target.value);
    };

    const handleCityChange = (e) => {
        setCity(e.target.value);
    };

    const handleDistrictChange = (e) => {
        setDistrict(e.target.value);
    };

    const handleEditClick = async () => {
        try {
            const response = await fetch("http://localhost:3006/edit/" + userId, {
                method: "POST",
                headers: { "Content-Type": "application/json" },
                body: JSON.stringify({
                    userId,
                    firstName,
                    lastName,
                    email,
                    city,
                    district,
                }),
            });
            //print the req.body
            console.log("req.body", JSON.stringify({ userId, firstName, lastName, email, city, district }));
            const data = await response.json();
            console.log(data);
            // Handle success or error response
            setIsProfileUpdated(true); // Set isProfileUpdated to true if no error occurs
        } catch (err) {
            console.log("Could not update user info");
        }
    };

    return (
        <div className="container mt-4">
            <div className="card border-danger p-4 rounded">
                <div className="card-body">
                    <h5 className="card-title text-danger border-bottom pb-2 mb-4">
                        <strong>User Information</strong>
                    </h5>
                    <p>
                        <strong>User ID:</strong> {userId}
                    </p>
                    <div>
                        <strong>First Name:</strong>{" "}
                        <input
                            type="text"
                            value={firstName}
                            onChange={handleFirstNameChange}
                        />
                    </div>
                    <div>
                        <strong>Last Name:</strong>{" "}
                        <input
                            type="text"
                            value={lastName}
                            onChange={handleLastNameChange}
                        />
                    </div>
                    <div>
                        <strong>Email:</strong>{" "}
                        <input type="text" value={email} onChange={handleEmailChange} />
                    </div>
                    <div>
                        <strong>City:</strong>{" "}
                        <input type="text" value={city} onChange={handleCityChange} />
                    </div>
                    <div>
                        <strong>District:</strong>{" "}
                        <input
                            type="text"
                            value={district}
                            onChange={handleDistrictChange}
                        />
                    </div>
                    <button onClick={handleEditClick}>OK</button>
                    {isProfileUpdated && (
                        <p style={{ color: "green", marginTop: "10px" }}>
                            Your Profile Has been Updated. Email May or May not be updated based on validity
                        </p>
                    )}
                </div>
            </div>
        </div>
    );
};

export default EditPage;
