import React, { useState, useEffect } from "react";
import CategoryList from "../components/CategoryList";
import UserInformation from "../routes/userInfo";
import { Link } from "react-router-dom";
import "../styles/Home.css";

const Home = ({ setAuth }) => {
  const [categories, setCategories] = useState([]);
  const [searchTerm, setSearchTerm] = useState("");

  useEffect(() => {
    const fetchCategories = async () => {
      try {
        const response = await fetch("http://localhost:3006/api/v1/categories");
        const data = await response.json();
        setCategories(data);
      } catch (error) {
        console.error("Error fetching categories:", error);
      }
    };
    fetchCategories();
  }, []);

  const handleSearchChange = (event) => {
    setSearchTerm(event.target.value);
    // You can perform search-related actions here
  };

  return (
    <div>
      <nav className="navbar">
        <div className="navbar__left">
          {/* Link to the user profile page */}
          <Link to="/userProfile">User Profile</Link>
        </div>

        <div className="navbar__right">
          {/* Dropdown menu for categories */}
          <select>
            {categories.map((category) => (
              <option key={category.id} value={category.name}>
                {category.name}
              </option>
            ))}
          </select>
          
          {/* Search input */}
          <input
            type="text"
            placeholder="Search..."
            value={searchTerm}
            onChange={handleSearchChange}
          />
        </div>
      </nav>

      {/* Conditional rendering of user information */}
    
      {/* Other content of the Home component */}
      {/* For example, CategoryList or other components */}
    </div>
  );
};

export default Home;
