import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import { useUserContext } from "../App";
import { useSearchParams } from 'react-router-dom';
const AddProductReview = () => {

  
  const [rating, setRating] = useState(1); // Initial rating set to 1
  const [comment, setComment] = useState('');
  const navigate = useNavigate();
  const { userId, setUserId } = useUserContext(); 

  const handleRatingIncrease = () => {
    if (rating < 5) {
      setRating(rating + 1);
    }
  };

  const handleRatingDecrease = () => {
    if (rating > 1) {
      setRating(rating - 1);
    }
  };

  const handleCommentChange = (event) => {
    setComment(event.target.value);
  };

  const handleSubmitReview = async () => {
    try {
      // Assuming you have an API endpoint to submit the review
      const response = await fetch('http://localhost:3006/addreviews/', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ rating, comment }),
      });

      if (!response.ok) {
        throw new Error('Failed to submit review');
      }

      console.log('Review submitted successfully');
      navigate('/'); // Redirect to home page after submitting review
    } catch (error) {
      console.error('Error submitting review:', error);
    }
  };

  return (
    <div className="add-review-container">
      <h2>Add Product Review</h2>
      <div>
        <label htmlFor="rating">Rating:</label>
        <button onClick={handleRatingDecrease}>-</button>
        <span>{rating}</span>
        <button onClick={handleRatingIncrease}>+</button>
      </div>
      <label htmlFor="comment">Comment:</label>
      <textarea
        id="comment"
        name="comment"
        value={comment}
        onChange={handleCommentChange}
        placeholder="Write your comment here..."
        required
      />
      <button className="submit-review-button" onClick={handleSubmitReview}>
        Submit Review
      </button>
    </div>
  );
};

export default AddProductReview;
