import React, { useState, useEffect } from 'react';

const SearchProducts = () => {
  const [searchByWhat, setSearchByWhat] = useState('');
  const [searchProduct, setSearchProduct] = useState('');
  const [products, setProducts] = useState([]);

  useEffect(() => {
    // Define an asynchronous function to fetch products for the specified category
    const fetchProducts = async () => {
      try {
        // Log a message indicating that products for the category are being fetched
        console.log('Fetching products for category:', searchProduct);

        // Define the base URL for the API endpoint
        const baseUrl = '/api/products';

        // Define the search query based on the selected search criteria
        let searchQuery = '';

        if (searchByWhat === 'name') {
          searchQuery = `?name=${searchProduct}`;
        } else if (searchByWhat === 'price') {
          searchQuery = `?price=${searchProduct}`;
        } else if (searchByWhat === 'rating') {
          searchQuery = `?rating=${searchProduct}`;
        }

        // Perform the API call to fetch products based on the search criteria
        const response = await fetch(baseUrl + searchQuery);

        // Check if the response is ok
        if (!response.ok) {
          throw new Error('Network response was not ok');
        }

        // Parse the JSON data from the response
        const data = await response.json();

        // Log the response and the parsed data
        console.log('Response:', response);
        console.log('Data:', data);

        // Update the products state with the fetched data
        setProducts(data);
      } catch (error) {
        // Log an error message if there's an error fetching products
        console.error('Error fetching products:', error);
      }
    };

    // Call the fetchProducts function when the searchByWhat or searchProduct state changes
    fetchProducts();
  }, [searchByWhat, searchProduct]); // Dependency array containing searchByWhat and searchProduct

  const handleSearchByWhatChange = (e) => {
    setSearchByWhat(e.target.value);
    setSearchProduct('');
  };

  const handleSearchProductChange = (e) => {
    setSearchProduct(e.target.value);
  };

  return (
    <div>
      <select value={searchByWhat} onChange={handleSearchByWhatChange}>
        <option value="">Select How to Search</option>
        <option value="name">Name</option>
        <option value="price">Price</option>
        <option value="rating">Rating</option>
      </select>

      {searchByWhat && (
        <input
          type="text"
          value={searchProduct}
          onChange={handleSearchProductChange}
          placeholder={`Search by ${searchByWhat}`}
        />
      )}

      <button onClick={() => setProducts([])}>Reset</button>

      {products.length > 0 && (
        <ul>
          {products.map((product) => (
            <li key={product.productid}>{product.name}</li>
          ))}
        </ul>
      )}
    </div>
  );
};

export default SearchProducts;
