import React, { useState, useEffect, useContext } from "react";
import { useUserContext } from "../App";

const UserInformation = ({ setAuth }) => {
  const { userId, setUserId } = useUserContext(); // Access userId from context

  const [firstName, setFirstName] = useState("");
  const [lastName, setLastName] = useState("");
  const [email, setEmail] = useState("");
  const [city, setCity] = useState("");
  const [district, setDistrict] = useState("");
  const [logoutClicked, setLogoutClicked] = useState(false);
  const [isLoggedIn, setIsLoggedIn] = useState(true);

  useEffect(() => {
    async function getUserInfo() {
      try {
        const response = await fetch("http://localhost:3006/dashboard/", {
          method: "GET",
          headers: { token: localStorage.token },
        });
        const userData = await response.json();
        console.log(userData);
        // Update states with received data
        setUserId(userData.userid);
        setFirstName(userData.firstname);
        setLastName(userData.lastname);
        setEmail(userData.email);
        setCity(userData.city);
        setDistrict(userData.district);
      } catch (err) {
        console.log("Could not fetch user info");
      }
    }
    getUserInfo();
  }, []);

  const buttonLogout = () => {
    console.log("Logout button clicked");
    // You can add your logout logic here
    try {
      localStorage.removeItem("token");
      setIsLoggedIn(false);
      setAuth(false);
    } catch (err) {
      console.error("Could not logout", err);
    }
  };

  return (
    <div className="user-info-container container mt-4"> {/* Apply Bootstrap container class and margin top */}
      <p className="user-info-heading"><strong>User Information</strong></p> {/* Apply Bootstrap strong tag style */}
      <p><strong>User ID:</strong> {userId}</p>
      <p><strong>First Name:</strong> {firstName}</p>
      <p><strong>Last Name:</strong> {lastName}</p>
      <p><strong>Email:</strong> {email}</p>
      <p><strong>City:</strong> {city}</p>
      <p><strong>District:</strong> {district}</p>
      {/* Add more user info attributes here */}
      {logoutClicked && <p>Logout button clicked</p>}
      <button className="btn btn-primary button-logout" onClick={buttonLogout}>Logout</button> {/* Apply Bootstrap button class and unique class name */}
    </div>
  );
};

export default UserInformation;
