import React, { useEffect, useState } from "react";
import { useNavigate } from "react-router-dom";

const categoryColors = [
  "#f0f0f0", // Light gray
  "#e0e0e0", // Lighter gray
  "#d0d0d0", // Lightest gray
  "#e6ffe6", // Light green
  "#fff0f0", // Light pink
  // Add more colors as needed
];

const CategoryList = () => {
  const navigate = useNavigate();
  const [categories, setCategories] = useState([]);

  useEffect(() => {
    const fetchData = async () => {
      try {
        const response = await fetch("http://localhost:3006/categories");
        const data = await response.json();
        setCategories(data);
      } catch (err) {
        console.log("Couldn't Fetch. What error? Categories");
      }
    };
    fetchData();
  }, []);

  function navigateToProducts(category) {
    navigate(`/productscategorywise/${category.categoryid}`);
  }

  return (
    <div className="category-container">
      <h3 className="mt-4">Categories</h3>
      <h5 className="text-grey">You can choose products from these categories:</h5>
      <div className="category-grid">
        {categories.map((category, index) => (
          <div
            key={category.categoryid}
            className="category-card"
            style={{ backgroundColor: categoryColors[index % categoryColors.length] }}
          >
            <div className="card-body">
              <h5 className="card-title">
                {category.categoryname} : {category.description}
              </h5>
              <h5 className="card-title">
                 {category.description}
              </h5>
              <button
                className="btn btn-primary"
                onClick={() => navigateToProducts(category)}
              >
                Go
              </button>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
};

export default CategoryList;
