import React, { useState, useEffect, useContext } from "react";
import { useUserContext } from "../App";
import { Link } from "react-router-dom"; // Import Link from react-router-dom

const EditPage = () => {
  const { userId, setUserId } = useUserContext(); // Access userId from context

  const [firstName, setFirstName] = useState("");
  const [lastName, setLastName] = useState("");
  const [email, setEmail] = useState("");
  const [city, setCity] = useState("");
  const [district, setDistrict] = useState("");
  const [logoutClicked, setLogoutClicked] = useState(false);
  const [isLoggedIn, setIsLoggedIn] = useState(true);

  useEffect(() => {
    async function getUserInfo() {
      try {
        const response = await fetch("http://localhost:3006/dashboard/", {
          method: "GET",
          headers: { token: localStorage.token },
        });
        const userData = await response.json();
        console.log(userData);
        // Update states with received data
        setUserId(userData.userid);
        setFirstName(userData.firstname);
        setLastName(userData.lastname);
        setEmail(userData.email);
        setCity(userData.city);
        setDistrict(userData.district);
      } catch (err) {
        console.log("Could not fetch user info");
      }
    }
    getUserInfo();
  }, []);

 
  return (
    <div className="container mt-4">
      <div className="card border-danger p-4 rounded">
        <div className="card-body">
          <h5 className="card-title text-danger border-bottom pb-2 mb-4">
            <strong>User Information</strong>
          </h5>
          <p>
            <strong>User ID:</strong> {userId}
          </p>
          <p>
            <strong>First Name:</strong> {firstName}
          </p>
          <p>
            <strong>Last Name:</strong> {lastName}
          </p>
          <p>
            <strong>Email:</strong> {email}
          </p>
          <p>
            <strong>City:</strong> {city}
          </p>
          <p>
            <strong>District:</strong> {district}
          </p>
          
          <Link to="/edit" className="btn btn-primary ml-2">Edit Profile</Link> {/* Add Link component with "/edit" route */}
        </div>
      </div>
    </div>
  );
};

export default EditPage;
