import React, { Fragment, useEffect, useState } from 'react';
import { useParams, Link } from 'react-router-dom';
import '../styles/ProductsCategoryWise.css'; // Import the CSS file

const ProductCategoryWise = ({ setAuth }) => {
  const { categoryid } = useParams();
  const [products, setProducts] = useState([]);

  useEffect(() => {
    const fetchProducts = async () => {
      try {
        console.log('Fetching products for category:', categoryid);
        const response = await fetch(`http://localhost:3006/products/${categoryid}`);
        const data = await response.json();
        console.log('Response:', response);
        console.log('Data:', data);
        setProducts(data);
      } catch (error) {
        console.error('Error fetching products:', error);
      }
    };

    fetchProducts();
  }, [categoryid]);

  return (
    <Fragment>
      <div className="container mt-4">
        <h1>ProductCategoryWise</h1>
        <p>Category ID: {categoryid}</p>
        <div className="row">
          {products.map((product) => (
            <div key={product.productid} className="col-md-4 mb-4">
              <div className="card">
                <div className="card-body">
                  <h5 className="card-title">{product.name}</h5>
                  <ul className="list-group list-group-flush">
                    <li className="list-group-item">Price: {product.price}</li>
                    <li className="list-group-item">Quantity in Stock: {product.quantityinstock}</li>
                    <li className="list-group-item">Category ID: {product.id}</li>
                  </ul>
                  <Link to={`/productDetails/${product.productid}`} className="btn btn-primary mt-2">View Details</Link>
                </div>
              </div>
            </div>
          ))}
        </div>
      </div>
    </Fragment>
  );
};

export default ProductCategoryWise;
