import React, { Fragment, useEffect, useState } from 'react';
import { useParams, Link } from 'react-router-dom';
// import '../styles/ProductsCategoryWise.css'; 
// Import the CSS file

const ProductCategoryWise = ({ setAuth }) => {
  const { id } = useParams();
  const [products, setProducts] = useState([]);

  useEffect(() => {
    const fetchProducts = async () => {
      try {
        console.log('Fetching products for category:', id);
        const response = await fetch(`http://localhost:3006/api/products/${id}`);
        const data = await response.json();
        console.log('Response:', response);
        console.log('Data:', data);
        setProducts(data);
      } catch (error) {
        console.error('Error fetching products:', error);
      }
    };

    fetchProducts();
  }, [id]);

  return (
    <Fragment>
      <div>
        <h1>ProductCategoryWise</h1>
        <p>Category ID: {id}</p>
        {products.map((product) => (
          <div key={product.productid} >
            <h2>{product.name}</h2>
            
              <li>Price: {product.price}</li>
              <li>Quantity in Stock: {product.quantityinstock}</li>
              <li>Category ID: {product.id}</li>
            
            <Link to={`/productDetails/${product.productid}`}>
              <button style={{ backgroundColor: 'blue', color: 'white', padding: '10px', borderRadius: '5px' }}>View Details</button>
            </Link>
          </div>
        ))}
      </div>
    </Fragment>
  );
};

export default ProductCategoryWise;
