import React, { Fragment, useEffect, useState } from 'react';
import { useParams, Link } from 'react-router-dom';

const ProductCategoryWise = () => {
  const { categoryid } = useParams();
  const [products, setProducts] = useState([]);

  useEffect(() => {
    const fetchProducts = async () => {
      try {
        console.log('Fetching products for category:', categoryid);
        const response = await fetch(`http://localhost:3006/products/${categoryid}`);
        const data = await response.json();
        console.log('Response:', response);
        console.log('Data:', data);
        setProducts(data);
      } catch (error) {
        console.error('Error fetching products:', error);
      }
    };

    fetchProducts();
  }, [categoryid]);

  return (
    <Fragment>
      <div className="product-container" style={{ marginTop: '200px' }}>
        <h1 className="product-title" style={{ color: '#3f51b5' }}>ProductCategoryWise</h1>
        <p className="category-info" style={{ color: '#4caf50' }}>Category ID: {categoryid}</p>
        <div className="product-grid">
          {products.map((product) => (
            <div key={product.productid} className="product-card" style={{ marginBottom: '20px' }}>
              <div className="product-card-content">
                <h3 className="product-name" style={{ color: '#f44336' }}>{product.name}</h3>
                <ul className="product-details" style={{ listStyle: 'none', padding: 0 }}>
                  <li className="product-detail">Price: <span className="product-price" style={{ color: '#ff9800' }}>{product.price}</span></li>
                  <li className="product-detail">Quantity in Stock: <span className="product-quantity" style={{ color: '#ffeb3b' }}>{product.quantityinstock}</span></li>
                  <li className="product-detail">Category ID: <span className="product-category-id" style={{ color: '#607d8b' }}>{product.categoryid}</span></li>
                </ul>
                <Link to={`/productDetails/${product.productid}`} className="details-link" style={{ backgroundColor: '#795548', color: '#fff', padding: '8px 16px', borderRadius: '4px', textDecoration: 'none', display: 'inline-block', marginTop: '10px' }}>View Details</Link>
              </div>
            </div>
          ))}
        </div>
      </div>
    </Fragment>
  );
};

export default ProductCategoryWise;
