import React, { useState, useEffect } from 'react';

const Cart = () => {

  const [products, setProducts] = useState([{ id: 1, name: 'Product 1', price: 100}, { id: 2, name: 'Product 2', price: 200}]);

  useEffect(() => {
  
    async function fetchCartProducts() {
      try {
        const response = await fetch('http://localhost:3006/api/cart');
        if (!response.ok) {
          throw new Error('Failed to fetch products');
        }
        const productsData = await response.json();
        setProducts(productsData);
      } catch (error) {
        console.error('Error fetching products:', error);
      }
    }
    

    fetchCartProducts();
  }, []);

  const handleRemoveProduct = async (productId) => {
    try {
      const response = await fetch(`http://localhost:3006/api/removeFromCart/${productId}`, {
        method: 'DELETE',
      });
      if (!response.ok) {
        throw new Error('Failed to delete product');
      }
      setProducts(products.filter(product => product.productid !== productId));
    } catch (error) {
      console.error('Error deleting product:', error);
    }
  };

  return (
    <div>
      <h1>Cart</h1>
      {products.map(product => (
        <div key={product.productid}>
          <p>{product.name} - ${product.price}</p>
          <button onClick={() => handleRemoveProduct(product.productid)}>Remove</button>
        </div>
      ))}
    </div>
  );
};

export default Cart;
